function timeFunc($function, $runs)\n{\n  $times = array();\n\n  for ($i = 0; $i < $runs; $i++)\n  {\n    $time = microtime();\n    call_user_func($function);\n    $times[$i] = microtime() - $time;\n  }\n\n  return array_sum($times) / $runs;\n}\n\nfunction Method1()\n{ \n  $foo = 'some words';\n  for ($i = 0; $i < 10000; $i++)\n    $t = "these are $foo";\n}\n\nfunction Method2()\n{\n  $foo = 'some words';\n  for ($i = 0; $i < 10000; $i++)\n    $t = "these are {$foo}";\n}\n\nfunction Method3()\n {\n  $foo = 'some words';\n  for ($i = 0; $i < 10000; $i++)\n    $t = "these are " . $foo;\n}\n\nprint timeFunc('Method1', 10) . "\n";\nprint timeFunc('Method2', 10) . "\n";\nprint timeFunc('Method3', 10) . "\n";