function procLogin($username,$password){\n    $query = "SELECT *\n         FROM members\n         WHERE login = '".mysql_escape_string($username)."'\n         AND passwd = '".mysql_escape_string($password)."'";\n    $result = mysql_query($query);\n\n    $row = mysql_fetch_assoc($result); \n\n    if ($row['member_id'] > 0)\n    {\n        return ($row['member_id']);\n    }\n    else\n    {\n        return false;\n    }\n}