public static function loadClass($class)\n{\n    $files = array(\n        $class . '.php',\n        str_replace('_', '/', $class) . '.php',\n    );\n    foreach (explode(PATH_SEPARATOR, ini_get('include_path')) as $base_path)\n    {\n        foreach ($files as $file)\n        {\n            $path = "$base_path/$file";\n            if (file_exists($path) && is_readable($path))\n            {\n                include_once $path;\n                return;\n            }\n        }\n    }\n}