\nclass A {\n   public $foo = 'bar';\n   private $private = array();\n\n   public function __get($key) {\n      echo 'Called __get() at line #' ,__LINE__, ' with key {', $key ,'}',"\n";\n      return $this->private[$key];\n   }\n\n   public function __set($key, $val) {\n      $this->private[$key] = $val;\n   }\n}\n\n\n$a = new A();\n\nvar_dump($a->foo);\n$a->bar = 'baz';\nvar_dump($a->bar);