<?php\n\n$mtime = microtime(); \n$mtime = explode(' ', $mtime); \n$mtime = $mtime[1] + $mtime[0]; \n$starttime = $mtime; \n\nfor ($i = 0; $i < 10000; $i++) {\n    include("foo.php");\n}\n\n\n$mtime = microtime(); \n$mtime = explode(" ", $mtime); \n$mtime = $mtime[1] + $mtime[0]; \n$endtime = $mtime; \n$totaltime = ($endtime - $starttime); \necho 'Rendered in ' .$totaltime. ' seconds.'; \n?>