<?php\n\nfunction gradual_output()\n{\n    # start using the output buffer.\n    ob_start();\n\n    for ($i = 0; $i <= 10; $i++) {\n        echo "loop count: " . $i . "<br />\n";\n\n        # flush the buffer to the user.\n        ob_flush(); flush();\n        \n        # do something that takes a while...\n        sleep(2);\n    }\n\n    # were done with the buffer, clean up.\n    ob_end_clean();\n}\n\ngradual_output();\n\n?>