<?php\n$db_conn = new COM("ADODB.Connection");\n$connstr = "DRIVER={Microsoft Access Driver (*.mdb)}; DBQ=". realpath("./Northwind.mdb").";";\n$db_conn->open($connstr);\n$rS = $db_conn->execute("SELECT * FROM Employees");\n$f1 =  $rS->Fields(0);\n$f2 =  $rS->Fields(1);\nwhile (!$rS->EOF)\n{\n    print $f1->value." ".$f2->value."<br />\n";\n    $rS->MoveNext();\n}\n$rS->Close();\n$db_conn->Close();\n?> 