$sql = "SELECT data_field FROM your_table WHERE data_field LIKE '%".$keyword."%'";\n    $res = mysql_query($sql);\n    while($row = mysql_fetch_array($res)) {\n        $data = $row['data_field'];         \n        $first_pos = strpos($data,$keyword);\n        if ($first_pos !== false) {\n                  $output = substr($data,max(0,$first_pos - 100),200 + strlen($keyword));\n          echo $output;\n        }\n    }