class NoMethods {\n    public function __call($name, $args)\n    {\n        echo __METHOD__ . " called $name with " . count($args) . " arguments.\n";\n        return $this;\n    }\n}\n\nclass Browser {\n    public function runThis()\n    {\n        echo __METHOD__ . "\n";\n        return $this;\n    }\n\n    public function stop()\n    {\n        echo __METHOD__ . "\n";\n        return new NoMethods();\n    }\n\n    public function dontRunThis()\n    {\n        echo __METHOD__ . "\n";\n        return $this;\n    }\n}\n\n$browser = new Browser();\necho "with stop\n";\n$browser->runThis()->stop()->dontRunThis()->dunno('hey');\necho "without stop\n";\n$browser->runThis()->dontRunThis();\necho "the end\n";