for($i = 0; $i <= count($fieldList); $i++)\n{\n    if (is_numeric($fieldList[$i]))\n    {\n        if (strpos($fieldList[$i],'.') !== false){\n            $fieldList[$i] = (int)$fieldList[$i];\n        }else{\n            $fieldList[$i] = (float)$fieldList[$i];\n        }\n    }\n\n    switch(gettype($fieldList[$i])) {\n        case 'integer':\n            $typeInfo = 'int(11)';\n            break;\n        case 'float':\n        case 'double':\n            $typeInfo = 'float';\n            break;\n\n        case 'string':\n            $typeInfo = 'varchar(80)';\n            break;\n        default:\n            $typeInfo = 'varchar(80)';\n            break;\n    }\nif(gettype($fieldList[$i]) != NULL) echo "\t".'`'.$i.'` '.$typeInfo.' NOT NULL, --'.gettype($fieldList[$i]).' '.$fieldList[$i]."<br/>\n";\n\n}