<?php\nclass cookieCheck\n{\n\n    public function check()\n    {\n        if (setcookie("test", "test", time() + 100))\n        {\n            //COOKIE IS SET \n            if (isset ($_COOKIE['test']))\n            {\n                return "Cookies are enabled on your browser";\n            }\n            else\n            {\n                return "Cookies are <b>NOT</b> enabled on your browser";\n            }\n        }\n\n    }\n}\n?>