function array_to_objecttree($array) {\n  if (is_numeric(key($array))) { // Because Filters->Filter should be an array\n    foreach ($array as $key => $value) {\n      $array[$key] = array_to_objecttree($value);\n    }\n    return $array;\n  }\n  $Object = new stdClass;\n  foreach ($array as $key => $value) {\n    if (is_array($value)) {\n      $Object->$key = array_to_objecttree($value);\n    }  else {\n      $Object->$key = $value;\n    }\n  }\n  return $Object;\n}