function getFileCount($path) {\n    $size = 0;\n    $ignore = array('.','..','cgi-bin','.DS_Store');\n    $files = scandir($path);\n    foreach($files as $t) {\n        if(in_array($t, $ignore)) continue;\n        if (is_dir(rtrim($path, '/') . '/' . $t)) {\n            $size += getFileCount(rtrim($path, '/') . '/' . $t);\n        } else {\n            $size++;\n        }   \n    }\n    return $size;\n}