function validateCurrency($string)\n{\n    return preg_match('/^\$?(\d{1,3})(,\d{3})*(.\d{2})?$/', $string) ||\n           preg_match('/^\$?\d+(.\d{2})?$/', $string);\n}\n\nfunction makeCurrency($string)\n{\n    $newstring = "";\n\n    $array = str_split($string);\n    foreach($array as $char)\n    {\n        if (($char >= '0' && $char <= '9') || $char == '.')\n        {\n            $newstring .= $char;\n        }\n    }\n\n    return $newstring;\n}