function relative_year_day($date) {\n    $value = date('z', strtotime($date)) - date('z');\n\n    if ($value < 0)\n        $value += 365;\n\n    return $value;\n}\n\nfunction cmp($a, $b)\n{\n    $aValue = relative_year_day($a);\n    $bValue = relative_year_day($b);\n\n    if ($aValue == $bValue)\n        return 0;\n\n    return ($aValue < $bValue) ? -1 : 1;\n}\n\n$a = array("1980-04-14", "2007-06-08",\n    "2008-12-25", "1978-11-03");\n\nusort($a, "cmp");