$d=date("D");\nif (in_array($d, array('Thu','Fri','Sat','Sun'))) {\n    echo "The latest post for Thursday, Friday, Saturday, Sunday: ";\n    $my_query = new WP_Query('category_name=posts&showposts=1');\n    while ($my_query->have_posts()) {\n        $my_query->the_post();\n        echo "<a href=\";\n        the_permalink();\n        echo "\" rel=\"bookmark\">";\n        the_title();\n        echo "</a>";\n    }\n}