function my_session_start($timeout = 1440) {\n    ini_set('session.gc_maxlifetime', $timeout);\n    session_start();\n\n    if (isset($_SESSION['timeout_idle']) && $_SESSION['timeout_idle'] < time()) {\n        session_destroy();\n        session_start();\n        session_regenerate_id();\n        $_SESSION = array();\n    }\n\n    $_SESSION['timeout_idle'] = time() + $timeout;\n}