$path = ".";\n$files = new DirectoryIterator($path);\n$files_array = array();\n\nwhile($files->valid()) {\n        // sort key, ie. modified timestamp\n        $key = $files->getMTime();\n        $data = $files->getFilename();\n        $files_array[$key] = $data;\n        $files->next();\n}\nksort($files_array);\nforeach($files_array as $key => $file){\n    print $key . " => " . $file . "\n";\n}