function extracting_comments($table, $fields,$condition,$order,$limit){\n                $query="SELECT ".$fields."\n                                FROM ".$table."\n                                WHERE ".$condition."\n                                ORDER BY ".$order."\n                                LIMIT ".$limit." ";\n                if($stmt = $this->conn->prepare($query)) {\n                        $stmt->execute();\n                        $row = array_pad(array(), $stmt->field_count, '');\n                        $params = array();\n                                foreach($row as $k=>$v) {\n                                  $params[] = &$row[$k];\n                                  echo $params[0];\n                                }\n                        call_user_func_array(array($stmt,'bind_result'),$params);\n                        $i=0;\n                        $result = array();\n                        while($stmt->fetch()) {\n                        $i++;\n                        foreach ($row as $b=>$elem) {\n                        $atul[$b]=$row[$b];\n                        }\n                        $result[]=$atul;\n                        }\n                        $stmt->close();\n                        return $result;\n                }\n\n        }