function makeList($par_id = 0) {\n    //your sql code here\n    $subsql("SELECT * FROM pages WHERE pag_parent = $par_id");\n    $pages = $dbch->fetchAll();\n\n    if (count($pages)) {\n        echo '<ul>';\n        foreach ($pages as $page) {\n            echo '<li>', $page['pag_title'];\n            makeList($page['pag_id']);\n            echo '</li>';\n        }\n        echo '</ul>';\n    }\n}