class Son {\n    protected $_father;\n    // ...\n    public function setFather($father) {\n        $this->_father = $father;\n    }\n\n    public function getFather() {\n        return $this->_father;\n    }\n}\n\nclass Father {\n    // ...\n    public function AddSon($son_id, $son_name, $son_age){\n        $sonHandler = new Son($son_id, $son_name, $son_age);\n        $sonHandler->setFather($this);\n        $this->sons[] = $sonHandler;\n        return $sonHandler;\n    }\n}