open = False\nfor line in lines do\n    nquotes = line.count("\"")\n    if not open then\n        if nquotes == 1 then\n            open = True\n            write(line)\n        else #we assume nquotes == 2\n            writeln(line)\n        end\n    else\n        if nquotes == 0 then\n            write(line)\n        else #we assume nquotes == 1\n            open = False\n            writeln(line)\n        end\n    end\n end