require "formatr"\ninclude FormatR\n\ntop_ex = <<DOT\n   Piggy Locations for @<< @#, @###\n                     month, day, year\n\nNumber: location              toe size\n-------------------------------------------\nDOT\n\nex = <<TOD\n@)      @<<<<<<<<<<<<<<<<       @#.##\nnum,    location,             toe_size\nTOD\n\nbody_fmt = Format.new (top_ex, ex)\n\nbody_fmt.setPageLength(10)\nnum = 1\n\nmonth = "Sep"\nday = 18\nyear = 2001\n["Market", "Home", "Eating Roast Beef", "Having None", "On the way home"].each {|location|\n    toe_size = (num * 3.5)\n    body_fmt.printFormat(binding)\n    num += 1\n}