describe "Long Island Iced Tea" do\n  serve_in 'Highball Glass'\n\n  ingredients do\n    half.ounce.of :vodka\n    half.ounce.of :tequila\n    half.ounce.of :light_rum\n    half.ounce.of :gin\n    1.dash.of :coca_cola\n    #ignoring lemon peel as how can a robot peel a lemon?\n  end\n\n  steps do\n    add :vodka, :tequila, :light_rum, :gin\n    stir :gently\n    add :coca_cola\n  end\nend