SELECT DISTINCT\n     T1.MyField1,\n     T1.MyField2,\n     T1.MyField3,\n     T1.MyField4,\n     T1.MyField5\nFROM\n     MyTable T1\nLEFT OUTER JOIN MyTable T2 ON\n     T2.MyField1 = T1.MyField1 AND\n     T2.MyField2 = T1.MyField2 AND\n     T2.MyField3 = T1.MyField3 AND\n     (\n          T2.MyField4 > T1.MyField4 OR\n          (\n               T2.MyField4 = T1.MyField4 AND\n               T2.MyField5 > T1.MyField5\n          )\n     )\nWHERE\n     T2.MyField1 IS NULL