With ResultTable (RowNumber\n                 ,ID\n                 ,Case_ID\n                 ,Name\n                 ,Date\n                 ,Status\n                 ,Type)\nAS\n(\n    SELECT Row_Number() OVER (PARTITION BY Case_ID\n                                  ORDER BY Date ASC)\n\n          ,ID\n          ,Case_ID\n          ,Name\n          ,Date\n          ,Status\n          ,Type\n      FROM CaseTable\n     WHERE Type   = 'Support'\n       AND Status = 'Start'\n)\n    SELECT ID\n          ,Case_ID\n          ,Name\n          ,Date\n          ,Status\n          ,Type\n      FROM ResultTable\n     WHERE RowNumber = 1