SELECT\n    t1.memberid,\n    t1.[name],\n    ISNULL(STUFF(\n      (\n        SELECT\n          ', ' + t2.interestName\n          FROM \n              table2 t2\n          INNER JOIN \n              table3 t3            \n              ON \n              t2.interestId = t3.interestId\n          WHERE \n              t3.memberid = t1.memberid\n          FOR XML PATH('')\n       ), 1, 2, ''\n    ), 'None') As interests\nFROM\n    table1 t1\nGROUP BY\n    t1.memberid,\nt1.[name]