SELECT\n  c.id            customer_id,\n  c.name          customer_name,\n  i.id            item_id,\n  i.name          item_name,\n  COUNT(i.id)     order_count,\n  SUM(i.quantity) items_ordered\nFROM\n  customers c\n  INNER JOIN orders o ON c.id = o.customer_id\n  INNER JOIN items  i ON i.id = o.order_id\nWHERE\n  i.name LIKE 'La%'\n  AND c.id = 1\nGROUP BY\n  c.id,\n  c.name,\n  i.id,\n  i.name