MERGE\nINTO    table1 t1\nUSING   table2 t2\nON      (SUBSTR (otname, INSTR (otname,'.', 1, 3)\n                         + 1, \n                         INSTR (otname, '.', 1, 4)\n                              - INSTR (otname,'.', 1, 3)\n                              - 1)\n                               = t2.sncode))\nWHEN MATCHED THEN\nUPDATE\nSET    t1.sncode = t2.sncode,\n       t1.description_text = t2.description_text