CREATE FUNCTION dbo.RemoveNonNumerics(@in VARCHAR(255))\nRETURNS VARCHAR(255)\nAS\nBEGIN\n    DECLARE @out VARCHAR(255)\n\n    IF (@in IS NOT NULL)\n    BEGIN\n        SET @out = ''\n\n        WHILE (@in <> '')\n        BEGIN\n            IF (@in LIKE '[0-9]%')\n                SET @out = @out + SUBSTRING(@in, 1, 1)\n\n            SET @in = SUBSTRING(@in, 2, LEN(@in) - 1)\n        END\n    END\n\n    RETURN(@out)\nEND