select *\n  from articles a\n  where a.article_id =\n      (select f.article_id\n        from articles f\n        where f.category_id = a.category_id\n        order by f.is_sticky, f.published_at\n        limit 1) or a.article_id =\n      (select f.article_id\n        from articles f\n        where f.category_id = a.category_id\n        order by f.is_sticky, f.published_at\n        limit 1, 1) or \n    a.article_id =\n      (select f.article_id\n        from articles f\n        where f.category_id = a.category_id\n        order by f.is_sticky, f.published_at\n        limit 2, 1)