SELECT *\n<br>FROM   (SELECT 1       AS ID,\n<br>               '1,2,3' AS MEMBERS\n<br>        UNION\n<br>        SELECT 2,\n<br>               '2'\n<br>        UNION\n<br>        SELECT 3,\n<br>               '3,1'\n<br>        UNION\n<br>        SELECT 4,\n<br>               '2,1') USERS\n<br>       LEFT JOIN (SELECT '1' AS MEMBER\n<br>                  UNION\n<br>                  SELECT '2'\n<br>                  UNION\n<br>                  SELECT '3'\n<br>                  UNION\n<br>                  SELECT '4') GROUPS\n<br>         ON CHARINDEX(',' + GROUPS.MEMBER + ',',',' + USERS.MEMBERS + ',') > 0</p>\n\n<p>