create table People\n(\n     ID        int          not null primary key auto_increment\n    ,FullName  varchar(255) not null\n    ,StateID   int \n    ,CountryID int \n    ,CityID    int \n)\n;\ncreate table States\n(\n     ID   int          not null primary key auto_increment\n    ,Name varchar(255) not null\n)\n;\ncreate table Countries\n(\n     ID   int          not null primary key auto_increment\n    ,Name varchar(255) not null\n)\n;\ncreate table Cities\n(\n     ID   int          not null primary key auto_increment\n    ,Name varchar(255) not null\n)\n;