SELECT\n    Col1, -- All of the columns you want to dedupe on\n    Col2, -- which is not neccesarily all of the columns\n    Col3, -- in the table\n    Col4,\n    Col5,\n    Col6,\n    Col7,\n    Col8,\n    Col9,\n    Col10\nFROM\n    MyTable\nGROUP BY\n    Col1,\n    Col2,\n    Col3,\n    Col4,\n    Col5,\n    Col6,\n    Col7,\n    Col8,\n    Col9,\n    Col10\nHAVING\n    COUNT(*) > 1