UPDATE SHIPMENT\nSET\n  OrgAddress1     = BD.OrgAddress1,\n  OrgAddress2     = BD.OrgAddress2,\n  OrgCity         = BD.OrgCity,\n  OrgState        = BD.OrgState,\n  OrgZip          = BD.OrgZip,\n  DestAddress1    = BD.DestAddress1,\n  DestAddress2    = BD.DestAddress2,\n  DestCity        = BD.DestCity,\n  DestState       = BD.DestState,\n  DestZip         = BD.DestZip\nFROM\n   BookingDetails BD\nWHERE \n   SHIPMENT.MyID2 = @MyID2\n   AND\n   BD.MyID = @MyID