SELECT   m.date, \n         s.symbol, \n         s.open, \n         s.high, \n         s.low, \n         s.close, \n         s.volume\nFROM     masterdate AS m LEFT OUTER JOIN \n         stockdata AS s ON m.date = s.date\nAND      s.symbol = 'Foo'\nWHERE    m.date >= (SELECT MIN(date) FROM stockdata WHERE symbol = 'Foo')\nAND      m.date <= (SELECT MAX(date) FROM stockdata WHERE symbol = 'Foo')