create function usf_GIS_GET_LAT(\n    @City varchar (30),\n    @State char (2)\n)\nreturns table\nas return (\n  select top 1 lat\n  from GIS_Location with (nolock) \n  where [State] = @State\n    and [City] = @City\n);\n\nGO\n\ncreate function usf_GIS_GET_LON (\n    @City varchar (30),\n    @State char (2)\n)\nreturns table\nas return (\n  select top 1 LON\n  from GIS_Location with (nolock)\n  where [State] = @State\n    and [City] = @City\n);