SELECT COUNT(*) AS numcommon, posts.pid, posts.post FROM posts\n               INNER JOIN p2t ON p2t.pid = posts.pid\n               WHERE p2t.tid IN\n               (SELECT p2t.tid FROM p2t\n               INNER JOIN posts ON p2t.pid = posts.pid\n               WHERE posts.pid = 1)\n               AND posts.pid != 1\n               GROUP BY posts.pid\n               ORDER BY numcommo