ALTER SYSTEM ENABLE RESTRICTED SESSION;\n\nbegin     \n    for x in (  \n            select Sid, Serial#, machine, program  \n            from v$session  \n            where  \n                machine <> 'MyDatabaseServerName'  \n        ) loop  \n        execute immediate 'Alter System Kill Session '''|| x.Sid  \n                     || ',' || x.Serial# || ''' IMMEDIATE';  \n    end loop;  \nend;