SELECT \n      COUNT(Page) AS VisitingCount\n    , CONVERT(VARCHAR(5),Date, 108) AS [Time] \nFROM\n    scr_SecuristLog   \nWHERE\n    Date BETWEEN '2009-05-04 00:00:00' AND '2009-05-06 14:58'  \n    AND\n    [user] IN (\n                SELECT \n                    USERNAME             \n                FROM\n                    scr_CustomerAuthorities \n                )  \nGROUP BY\n    CONVERT(VARCHAR(5),Date, 108) \nORDER BY\n    [VisitingCount] ASC 