CREATE PROCEDURE prc_update (@table1_id INT, @table2_id INT, @table3_id INT, @new_value INT)\nAS\nBEGIN\n        UPDATE  Table1\n        SET     field1 = @new_value\n        WHERE   id = @table1_id\n\n        UPDATE  Table2\n        SET     field2 = @new_value\n        WHERE   id = @table2_id\n\n        UPDATE  Table3\n        SET     field3 = @new_value\n        WHERE   id = @table3_id\nEND