begin\n    begin\n        select data into v_data from table1;\n    exception\n        when no_data_found then\n        begin\n            select data into v_data from table2;\n        exception\n            when no_data_found then\n            begin\n               select data into v_data from table3;\n            exception\n                when no_data_found then\n                    v_data := null;\n            end;\n        end;\n    end;\n    return v_data;\nend;