select m1.id\n      , m2.name\n   from (select min(t1.rowid) as min_rowid\n              , t1.id\n           from table1 t1\n          where t1.id is not null\n          group by t1.id\n        ) m1\n      , (select min(t2.rowid) as min_rowid\n             , t2.name from table1 t2\n         where t2.name is not null\n         group by t2.name\n        ) m2\n  where m1.min_rowid = m2.min_rowid\n  order\n     by m1.id