SELECT rocks.name,\n       rocks.latitude,\n       rocks.longitude,\n       rocks.indoor,\n       rocks.address,\n       rocks.phone,\n       rocks.email,\n       rocks.website,\n       rocks.closed,\n       rock_types.type\n\nFROM   rocks,\n       rock_types\n\nWHERE  rocks.latitude  > -180                 AND\n       rocks.latitude  < 180                  AND\n       rocks.longitude > -180                 AND\n       rocks.longitude < 180                  AND\n       rocks.type_id   = rocks_types.id       AND\n       rock_types.type IN ('DWS', 'Top rope')