create procedure YourReportProcedure\n(\n     @ReportVersion    char(1)\n    ,@filterParam1     varchar(12)\n    ,@filterParam2     int\n    ....\n)\n\nif @ReportVersion='A'\nBEGIN\n    SELECT\n        A,B,C\n        FROM .....\n        WHERE x=@filterParam1 and y=@filterParam2\n\nEND\nELSE IF@ReportVersion='C'\nBEGIN\n    SELECT\n        A,B,C\n        FROM .....\n        WHERE g>@filterParam1 and r<@filterParam2\n\nEND\nELSE\nBEGIN\n    return 1 --error\nEND\n\nreturn 0\n\ngo