WITH hier(num, lvl) AS (\n        SELECT  0, 1\n        UNION ALL\n        SELECT  100, 1\n        UNION ALL \n        SELECT  num + 1, lvl + 1\n        FROM    hier\n        WHERE   lvl < 100\n        )\nSELECT  DATEADD(dw, -DATEPART(dw, '29.05.2007'), '29.05.2007') + num  * 7,\n        DATEADD(dw, -DATEPART(dw, '29.05.2007'), '29.05.2007') + (num + 1) * 7\nFROM    hier\nWHERE   DATEADD(dw, -DATEPART(dw, '29.05.2007'), '29.05.2007') + num * 7 < '29.05.2009' \nORDER BY\n        num