CREATE INDEX ::=\n\n    CREATE [ UNIQUE | BITMAP ] INDEX [ schema. ]index\n      ON { cluster_index_clause\n         | table_index_clause\n         | bitmap_join_index_clause\n         } ;\n\ntable_index_clause ::=\n\n    [ schema. ]table [ t_alias ]\n    (index_expr [ ASC | DESC ]\n      [, index_expr [ ASC | DESC ] ]...)\n    [ index_properties ]\n\nindex_expr ::= { column | column_expression }