begin transaction;\n\n   declare @deletedIds table ( id int );\n\n   delete from t1\n   output deleted.id into @deletedIds\n   from table1 as t1\n    inner join table2 as t2\n      on t2.id = t1.id\n    inner join table3 as t3\n      on t3.id = t2.id;\n\n   delete from t2\n   from table2 as t2\n    inner join @deletedIds as d\n      on d.id = t2.id;\n\n   delete from t3\n   from table3 as t3 ...\n\ncommit transaction;