DECLARE @PageNum AS INT;\nDECLARE @PageSize AS INT;\nSET @PageNum = 2;\nSET @PageSize = 10;\n\nWITH OrdersRN AS\n(\n    SELECT ROW_NUMBER() OVER(ORDER BY OrderDate, OrderID) AS RowNum\n          ,OrderID\n          ,OrderDate\n          ,CustomerID\n          ,EmployeeID\n      FROM dbo.Orders\n)\n\nSELECT * \n  FROM OrdersRN\n WHERE RowNum BETWEEN (@PageNum - 1) * @PageSize + 1 \n                  AND @PageNum * @PageSize\n ORDER BY OrderDate\n         ,OrderID;