DECLARE @RefID int, @Changes bit\n\nSET @Changes = 0 --No changes by default\n\nSET @RefID = 42 --Your RefID\n\nIF EXISTS(SELECT * FROM MyDetailTable\n          WHERE RefID = @RefID\n          AND (\n          (Col1 IS NULL AND NewCol1 IS NOT NULL)\n          OR \n          (Col1 IS NOT NULL AND NewCol1 IS NULL)\n          OR\n          (Col1 <> Col2)\n          ))\n   SET @Changes = 1